import { Component, Input, Output, EventEmitter } from "@angular/core";

@Component({
  selector: "app-test",
  templateUrl: "./test.component.html",
  styleUrls: ["./test.component.css"],
})
export class TestComponent {
  @Input() maxRating: number = 5;
  @Input() initialRating: number = 0;
  @Output() ratingChange: EventEmitter<number> = new EventEmitter<number>();

  currentRating!: number;
  stars!: number[];

  ngOnInit() {
    this.currentRating = this.initialRating;
    this.stars = Array(this.maxRating)
      .fill(0)
      .map((_, i) => i + 1);
  }

  setRating(rating: number) {
    this.currentRating = rating;
    this.ratingChange.emit(this.currentRating);
  }

  getRating(event: MouseEvent, starIndex: number) {
    const rating = starIndex + 1;
    this.setRating(rating);
  }
}
<div class="container">
  <h1>Forum</h1>

  <!-- Post Form -->
  <div class="card">
    <div class="card-header">Create New Post</div>
    <div class="card-body">
      <form>
        <div class="form-group">
          <label for="postTitle">Title</label>
          <input
            type="text"
            class="form-control"
            id="postTitle"
            placeholder="Enter post title"
          />
        </div>
        <div class="form-group">
          <label for="postContent">Content</label>
          <textarea
            class="form-control"
            id="postContent"
            rows="3"
            placeholder="Enter post content"
          ></textarea>
        </div>
        <button type="submit" class="btn btn-primary">Submit</button>
      </form>
    </div>
  </div>

  <!-- Post List -->
  <div class="card mt-4">
    <div class="card-header">Latest Posts</div>
    <div class="card-body">
      <div class="media mb-3">
        <img src="..." class="mr-3" alt="User Avatar" />
        <div class="media-body">
          <h5 class="mt-0">Post Title</h5>
          <p>Post Content</p>
          <button class="btn btn-sm btn-secondary">Comment</button>
        </div>
      </div>
      <div class="media">
        <img src="..." class="mr-3" alt="User Avatar" />
        <div class="media-body">
          <h5 class="mt-0">Post Title</h5>
          <p>Post Content</p>
          <button class="btn btn-sm btn-secondary">Comment</button>
        </div>
      </div>
    </div>
  </div>
</div>
