import { Component, OnInit } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { HttpClient } from "@angular/common/http";
import { ToastrService } from "ngx-toastr";

@Component({
  selector: "app-accueil",
  templateUrl: "./accueil.component.html",
  styleUrls: ["./accueil.component.css"],
})
export class AccueilComponent implements OnInit {
  constructor(
    private route: ActivatedRoute,
    private http: HttpClient,
    private toastr: ToastrService
  ) {}

  ngOnInit(): void {
    //parallax effect
    const parallax = document.getElementById("parallax");
    window.addEventListener("scroll", () => {
      let offset = window.pageYOffset;
      if (parallax) {
        parallax.style.backgroundPositionY = offset * 0.7 + "px";
      }
    });

    // Check if token exists in the URL
    const token = this.route.snapshot.paramMap.get("token");
    if (token) {
      this.confirmEmail(token);
    }

    //logwout message :
    const message = sessionStorage.getItem("notificationMessage");
    const type = sessionStorage.getItem("notificationType");

    sessionStorage.removeItem("notificationMessage");
    sessionStorage.removeItem("notificationType");

    if (message && type) {
      if (type === "success") {
        this.toastr.success(message);
      } else if (type === "error") {
        this.toastr.error(message);
      } else if (type === "warning") {
        this.toastr.warning(message);
      } else if (type === "info") {
        this.toastr.info(message);
      }
    }
  }

  scrollToSection(targetId: string): void {
    const targetSection = document.getElementById(targetId);
    if (targetSection) {
      targetSection.scrollIntoView({ behavior: "smooth" });
    }
  }

  confirmEmail(token: string): void {
    this.http.get(`http://localhost:4000/confirm-email/${token}`).subscribe(
      (response: any) => {
        console.log("Email confirmed successfully!");

        this.toastr.success(
          response.message || "Email confirmed successfully!",
          "Success"
        );
      },
      (error: any) => {
        console.log("Error confirming email:", error);

        this.toastr.error(
          error.error ||
            "Une erreur s'est produite lors de la confirmation de l'e-mail.",
          "Error"
        );
      }
    );
  }
}
<section>
  <div class="parallax-first">
    <app-navbar></app-navbar>

    <div class="container">
      <div class="background-image">
        <h1 class="welcome-text">Welcome to 13 BY AMU</h1>
      </div>
    </div>

    <div class="containerr">
      <div class="box" (click)="scrollToSection('target-section-1')">
        <div class="icon">
          <span class="icon"><i class="bx bx-football"></i></span>
        </div>
        <div class="text">
          <h3>Tout sur le sport</h3>
        </div>
      </div>
      <div class="box" (click)="scrollToSection('target-section-2')">
        <div class="icon">
          <span class="icon"><i class="bx bx-church"></i></span>
        </div>
        <div class="text">
          <h3>MARSEILLE</h3>
        </div>
      </div>
      <div class="box" (click)="scrollToSection('target-section-3')">
        <div class="icon">
          <span class="icon"><i class="bx bx-question-mark"></i></span>
        </div>
        <div class="text">
          <h3>À propos de nous</h3>
        </div>
      </div>
    </div>
  </div>

  <div id="target-section-1" class="parallax-page">
    <div class="content-wrapper">
      <div class="image-container">
        <img src="../../assets/GQ_50Greatest_final_v2.webp" alt="Photo" />
      </div>
      <div class="text-container custom-font">
        <h3><i class="bx bx-trophy"></i>Tout sur le sport</h3>
        <p class="larger-text">
          Le sport occupe une place centrale dans notre site web en raison de
          son importance multidimensionnelle. Tout d'abord, il contribue à notre
          santé et notre bien-être en favorisant l'activité physique régulière,
          renforçant notre système cardiovasculaire et prévenant certaines
          maladies.
        </p>

        <p class="larger-text">
          De plus, le sport favorise la socialisation et l'esprit d'équipe en
          permettant les rencontres, les échanges et le développement de
          compétences sociales. En outre, il joue un rôle crucial dans la
          gestion du stress, en libérant des endorphines et en favorisant le
          bien-être mental. Enfin, le sport stimule la confiance en soi, le
          dépassement de soi, et offre des moments de divertissement, de passion
          et de plaisir pour tous les amateurs.
        </p>
      </div>
    </div>
  </div>
  <div id="target-section-2" class="parallax-page">
    <div class="content-wrapper1">
      <div class="image-container1">
        <img src="../../assets/mrs.jpg" alt="Photo" />
      </div>
      <div class="text-container custom-font">
        <h3><i class="bx bx-church"></i>Marseille</h3>
        <p class="larger-text">
          Bienvenue sur notre site dédié à Marseille, la magnifique ville
          ensoleillée de la côte méditerranéenne. Nous sommes ravis de vous
          présenter notre service de réservation unique qui vous permettra de
          profiter pleinement de tout ce que Marseille a à offrir.
        </p>

        <p class="larger-text">
          Notre objectif est de vous aider à créer des souvenirs mémorables et à
          vivre des expériences uniques à Marseille.
        </p>
        <p class="larger-text">
          Réservez dès maintenant et plongez dans l'essence vibrante de
          Marseille !
        </p>
      </div>
    </div>
  </div>
  <div id="target-section-3" class="parallax-page">
    <h2>À propos de nous</h2>
  </div>
</section>
