import { Component, OnInit } from "@angular/core";
import { FormControl, FormGroup, Validators } from "@angular/forms";
import { ForumService } from "src/app/services/forum.service";
import { Router } from "@angular/router";
import { ToastrService } from "ngx-toastr";
import { environment } from "src/environment/environment";
import { HttpClient } from "@angular/common/http";
import { ConnectionService } from "src/app/services/connection.service";

@Component({
  selector: "app-forum",
  templateUrl: "./forum.component.html",
  styleUrls: ["./forum.component.css"],
})
export class ForumComponent implements OnInit {
  url_publication = environment.api_url + "/publication";
  url_comment = environment.api_url + "/comment";

  isLoggedIn!: boolean;

  publications: any[] = [];
  comments: any[] = [];

  publicationForm: FormGroup = new FormGroup({
    title: new FormControl("", [Validators.required, Validators.minLength(5)]),
    publication: new FormControl("", [
      Validators.required,
      Validators.minLength(10),
    ]),
  });

  commentForm: FormGroup = new FormGroup({
    comment: new FormControl("", [
      Validators.required,
      Validators.minLength(10),
    ]),
  });

  constructor(
    private forumservice: ForumService,
    private router: Router,
    private toastr: ToastrService,
    private http: HttpClient,
    private connectionService: ConnectionService
  ) {}

  ngOnInit(): void {
    this.connectionService.isUserLoggedIn$.subscribe(
      (loggedIn) => (this.isLoggedIn = loggedIn)
    );

    if (this.isLoggedIn == false) {
      this.router.navigate(["/log"]);
    }

    this.getPublications();
    this.getComments();
  }

  getPublications(): void {
    this.http.get<any[]>(this.url_publication).subscribe(
      (publicationsWithUserInfo) => {
        this.publications = publicationsWithUserInfo;
        console.log(this.publications);
      },
      (error) => {
        console.error(error);
      }
    );
  }

  getComments() {
    this.http.get<any[]>(this.url_comment).subscribe(
      (comments) => {
        this.comments = comments;
        console.log(this.comments);
      },
      (error) => {
        console.error(error);
      }
    );
  }

  submit() {
    const value = this.publicationForm.value;
    this.forumservice.poste(value).subscribe((res) => {
      if (res.success) {
        this.router.navigate([res.redirectUrl]);
        this.toastr.success("Publication ajouté avec succès", "Succès");
      }
      setTimeout(() => {
        location.reload();
      }, 1000);
    });
  }

  submitComment(idPublication: number) {
    const value = this.commentForm.value;
    this.forumservice.posteComment(value, idPublication).subscribe((res) => {
      if (res.success) {
        this.router.navigate([res.redirectUrl]);
        this.toastr.success("Commentaire ajouté avec succès", "Succès");
      }
      setTimeout(() => {
        location.reload();
      }, 1000);
    });
  }

  showForm = false;
  title = "";
  publication = "";

  cancelForm(): void {
    this.title = "";
    this.publication = "";
    this.showForm = false;
  }

  selectedPublication: any = null;
  openPopup(publication: any): void {
    this.selectedPublication = publication;
  }

  closePopup(): void {
    this.selectedPublication = null;
  }

  filterByDate(selectedValue: string) {
    const currentDate = new Date();
    if (selectedValue === "week") {
      // Show publications for this week
      const currentWeekStart = new Date(
        currentDate.getFullYear(),
        currentDate.getMonth(),
        currentDate.getDate() - currentDate.getDay()
      );
      const currentWeekEnd = new Date(
        currentDate.getFullYear(),
        currentDate.getMonth(),
        currentDate.getDate() + (6 - currentDate.getDay())
      );

      this.publications.forEach((publication) => {
        const publicationDate = new Date(publication.datePublication);
        publication.show =
          publicationDate >= currentWeekStart &&
          publicationDate <= currentWeekEnd;
      });
    } else if (selectedValue === "month") {
      // Show publications for this month
      const currentMonthStart = new Date(
        currentDate.getFullYear(),
        currentDate.getMonth(),
        1
      );
      const currentMonthEnd = new Date(
        currentDate.getFullYear(),
        currentDate.getMonth() + 1,
        0
      );

      this.publications.forEach((publication) => {
        const publicationDate = new Date(publication.datePublication);
        publication.show =
          publicationDate >= currentMonthStart &&
          publicationDate <= currentMonthEnd;
      });
    } else if (selectedValue === "year") {
      // Show publications for this year
      const currentYearStart = new Date(currentDate.getFullYear(), 0, 1);
      const currentYearEnd = new Date(currentDate.getFullYear(), 11, 31);

      this.publications.forEach((publication) => {
        const publicationDate = new Date(publication.datePublication);
        publication.show =
          publicationDate >= currentYearStart &&
          publicationDate <= currentYearEnd;
      });
    } else {
      // Show all publications
      this.publications.forEach((publication) => {
        publication.show = true;
      });
    }
  }

  filterByType(selectedValue: string) {
    const sessionData = JSON.parse(
      sessionStorage.getItem("sessionData") || "{}"
    );
    const userId = sessionData.idUser;

    if (selectedValue === "all") {
      // Show all publications
      this.publications.forEach((publication) => {
        publication.show = true;
      });
    } else if (selectedValue === "my") {
      // Show only user's publications
      this.publications.forEach((publication) => {
        publication.show = publication.idUser === userId;
      });
    }
  }
}
