import { Component, Renderer2, OnInit } from "@angular/core";
import { ConnectionService } from "src/app/services/connection.service";
import { ToastrService } from "ngx-toastr";
import { Router } from "@angular/router";
import { NavigationEnd } from "@angular/router";

@Component({
  selector: "app-sidebar",
  templateUrl: "./sidebar.component.html",
  styleUrls: ["./sidebar.component.css"],
})
export class SidebarComponent {
  isProfilePage!: boolean;

  constructor(
    private connectionService: ConnectionService,
    private toastr: ToastrService,
    private renderer: Renderer2,
    private router: Router
  ) {
    this.router.events.subscribe((event) => {
      if (event instanceof NavigationEnd) {
        this.isProfilePage = event.url === "/profile";
      }
    });
  }

  logout() {
    this.connectionService.logout();
  }
}
<ul class="menu">
  <li title="Dashboard">
    <a
      href="#"
      [ngClass]="{ dash: true, 'profile-page': isProfilePage }"
      routerLink="/profile"
      >Dashboard</a
    >
  </li>
  <li title="Change Password">
    <a
      href="#"
      [ngClass]="{ change: true, 'profile-page': !isProfilePage }"
      routerLink="/changePassword"
      >Change Password</a
    >
  </li>
  <li title="Log Out" (click)="logout()">
    <a href="#" class="logout" (click)="logout()">Log Out</a>
  </li>
</ul>
