import { Component, OnInit } from "@angular/core";
import { ConnectionService } from "src/app/services/connection.service";
import { Router } from "@angular/router";
import { environment } from "src/environment/environment";
import { HttpClient } from "@angular/common/http";

@Component({
  selector: "app-mesreservations",
  templateUrl: "./mesreservations.component.html",
  styleUrls: ["./mesreservations.component.css"],
})
export class MesreservationsComponent implements OnInit {
  isLoggedIn!: boolean;
  url = environment.api_url + "/mesreservations";
  url_cancel = environment.api_url + "/cancel";
  reservations: any[] = [];

  constructor(
    private connectionService: ConnectionService,
    private router: Router,
    private http: HttpClient
  ) {}

  ngOnInit(): void {
    this.connectionService.isUserLoggedIn$.subscribe(
      (loggedIn) => (this.isLoggedIn = loggedIn)
    );

    if (this.isLoggedIn == false) {
      this.router.navigate(["/accueil"]);
    }
    this.getReservations();
  }

  getReservations(): void {
    const sessionData = JSON.parse(
      sessionStorage.getItem("sessionData") || "{}"
    );
    if (sessionData && sessionData.idUser) {
      const options = { params: { idUser: sessionData.idUser } };
      this.http.get<any[]>(this.url, options).subscribe(
        (reservationContents) => {
          this.reservations = reservationContents;
          console.log(reservationContents);
        },
        (error) => {
          console.error(error);
        }
      );
    }
  }

  cancelReservation(idReservation: string) {
    const payload = {
      idReservation: idReservation,
    };

    this.http.post(this.url_cancel, payload).subscribe((res) => {
      if (res) {
        console.log("Cancellation successful:", res);
      }
    });
  }
}
<app-navbar></app-navbar> <br /><br />
<table>
  <thead>
    <tr>
      <th>Nom du terrain</th>
      <th>Date</th>
      <th>Heure de début</th>
      <th>Heure de fin</th>
      <th></th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let reservation of reservations">
      <td>{{ reservation[0].nameTerrain }}</td>
      <td>{{ reservation[0].dateReservation }}</td>
      <td>{{ reservation[0].startTime }}</td>
      <td>{{ reservation[0].endTime }}</td>
      <td>
        <button
          *ngIf="reservation[0].isCanceled === 0"
          (click)="cancelReservation(reservation[0].idReservation)"
        >
          Cancel
        </button>
      </td>
    </tr>
  </tbody>
</table>
