import { Component, OnInit } from "@angular/core";
import { FormControl, FormGroup, Validators } from "@angular/forms";
import { ForumService } from "src/app/services/forum.service";
import { Router } from "@angular/router";
import { ToastrService } from "ngx-toastr";
import { environment } from "src/environment/environment";
import { HttpClient } from "@angular/common/http";
import { ConnectionService } from "src/app/services/connection.service";

@Component({
  selector: "app-forum",
  templateUrl: "./forum.component.html",
  styleUrls: ["./forum.component.css"],
})
export class ForumComponent implements OnInit {
  url = environment.api_url + "/publication";
  isLoggedIn!: boolean;

  publications: any[] = [];

  publicationForm: FormGroup = new FormGroup({
    title: new FormControl("", [Validators.required, Validators.minLength(5)]),
    publication: new FormControl("", [
      Validators.required,
      Validators.minLength(10),
    ]),
  });

  constructor(
    private forumservice: ForumService,
    private router: Router,
    private toastr: ToastrService,
    private http: HttpClient,
    private connectionService: ConnectionService
  ) {}

  ngOnInit(): void {
    this.connectionService.isUserLoggedIn$.subscribe(
      (loggedIn) => (this.isLoggedIn = loggedIn)
    );

    if (this.isLoggedIn == false) {
      this.router.navigate(["/log"]);
    }

    this.getPublications();
  }

  getPublications(): void {
    console.log("hello1");
    this.http.get<any[]>(this.url).subscribe(
      (publicationsWithUserInfo) => {
        console.log("test");
        this.publications = publicationsWithUserInfo;
        console.log(this.publications);
      },
      (error) => {
        console.error(error);
      }
    );
  }

  submit() {
    const value = this.publicationForm.value;
    this.forumservice.poste(value).subscribe((res) => {
      if (res.success) {
        this.router.navigate([res.redirectUrl]);
        this.toastr.success("Publication ajouté avec succès", "Succès");
      }
      setTimeout(() => {
        location.reload();
      }, 1000);
    });
  }

  showForm = false;
  title = "";
  publication = "";

  submitForm(): void {
    // Handle form submission
    console.log("Title:", this.title);
    console.log("Publication:", this.publication);

    // Reset form values and hide the form
    this.title = "";
    this.publication = "";
    this.showForm = false;
  }

  cancelForm(): void {
    // Reset form values and hide the form
    this.title = "";
    this.publication = "";
    this.showForm = false;
  }

  selectedPublication: any = null;
  openPopup(publication: any): void {
    this.selectedPublication = publication;
  }

  closePopup(): void {
    this.selectedPublication = null;
  }
}
<div class="navbar-container">
  <app-navbar></app-navbar>
</div>

<div class="sidebar">
  <button class="reserve" (click)="showForm = true">
    Écrire une publication
  </button>
  <!-- Add filter options here -->
</div>

<div class="publication-container">
  <div
    class="publication"
    *ngFor="let publication of publications"
    (click)="openPopup(publication)"
  >
    <div class="triangle"></div>
    <div class="user-info">
      <div class="user-photo">
        <img [src]="publication.userPhoto" alt="User Photo" />
      </div>
      <div class="user-details">
        <div class="user-name">
          {{ publication.firstName }} {{ publication.lastName }}
        </div>
        <div class="publication-title">{{ publication.titre }}</div>
      </div>
    </div>
  </div>
</div>

<div class="popupf" *ngIf="showForm">
  <form (submit)="submit()" [formGroup]="publicationForm">
    <label for="title">Title:</label>
    <input type="text" id="title" name="title" formControlName="title" />

    <label for="publication">Publication:</label>
    <textarea
      id="publication"
      name="publication"
      formControlName="publication"
    ></textarea>

    <div class="buttons">
      <button type="submit">Submit</button>
      <button type="button" (click)="cancelForm()">Cancel</button>
    </div>
  </form>
</div>

<div class="popup" *ngIf="selectedPublication">
  <div class="popup-content">
    <span class="close" (click)="closePopup()">&times;</span>
    <h2>{{ selectedPublication.titre }}</h2>
    <p>{{ selectedPublication.content }}</p>
  </div>
</div>
